INSERT INTO `shop_goods_data` VALUES ('29', '原创解构型棉麻微哈伦小脚分裤', '\r\n\r\n1、裤脚罗文设计，增强弹力，有效塑紧小腿。\r\n\r\n2、个性的哈伦效果，活力而舒适。\r\n\r\n3、结构形的设计，文艺而时尚。\r\n\r\n \r\n\r\n面料介绍：\r\n\r\n亚麻纺织品由于有吸湿性好、无静电、保暖性强、抗拉力高、抗腐耐热、平直光洁、光泽柔和、纤维柔软，波浪循环的几大特点和圣洁、大方、卫生、自然的五种风格，还由于它产量不高等特点，在人们印象中形成了“亚麻就代表高档”观念。人们在穿过毛料、棉布，化纤布之后穿着趋向返璞归真的自然风格，纤维中的皇后——亚麻纤维品正适合了人们的这种需求。亚麻纺织品作为生态流行服装有全球再次掀起热潮之势，亚麻制品将带给人们一个最贴身的保健生活环境，提高人们的生活质量。\r\n', '<p><img style=\"display: inline;\" src=\"http://i1.mbscss.com/meter/image/suorang/14/2/185014254-SH.jpg\" data-original=\"http://i1.mbscss.com/meter/image/suorang/14/2/185014254-SH.jpg\" alt=\"\" height=\"625\" width=\"741\"/></p><p><img style=\"display: block;\" src=\"http://i0.mbscss.com/meter/image/suorang/14/2/185014254-CO-015-01.jpg\" data-original=\"http://i0.mbscss.com/meter/image/suorang/14/2/185014254-CO-015-01.jpg\" alt=\"\" height=\"762\" width=\"563\"/>\r\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><img style=\"display: block;\" src=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-CO-050-02.jpg\" data-original=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-CO-050-02.jpg\" alt=\"\" height=\"762\" width=\"563\"/> <br/></p><p><img style=\"display: inline;\" src=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-PE-01.jpg\" data-original=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-PE-01.jpg\" alt=\"\" height=\"733\" width=\"541\"/></p><p><img style=\"display: inline;\" src=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-PE-02.jpg\" data-original=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-PE-02.jpg\" alt=\"\" height=\"733\" width=\"541\"/></p><p><img style=\"display: inline;\" src=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-PE-03.jpg\" data-original=\"http://i2.mbscss.com/meter/image/suorang/14/2/185014254-PE-03.jpg\" alt=\"\" height=\"733\" width=\"541\"/></p><p><img style=\"display: inline;\" src=\"http://i1.mbscss.com/meter/image/suorang/14/2/185014254-PE-04.jpg\" data-